export const ContactsList = ({ contacts, onDeleteContact }) => {
  return (
    <ul>
      {contacts.map(({ id, name, number }) => {
        return (
          <li key={id}>
            {name}: {number}
            <button
              onClick={() => onDeleteContact(id)}
              style={{
                display: "block",
                width: "100px",
                backgroundColor: "transparent",
                borderRadius: "5px",
                padding: "5px",
                cursor: "pointer",
              }}
            >
              Remove
            </button>
          </li>
        );
      })}
    </ul>
  );
};
